import environ
import settings

def list_of_tuples(*args, **kwargs):
  default = kwargs.pop('default', [])
  l = env(*args, default=None)
  if not l:
    return default
  n = 2
  assert len(l) % n == 0
  res = []
  for i in range(0, len(l), n):
    res.append(l[i: i + n])
  return res

